import React, { Component } from "react";
import { OrgChart } from "d3-org-chart";
import user from "./user-black-shape.png";
import company from "./home-page.png";


export default class OrgChartComponent extends Component {
	constructor(props) {
		super(props);
		this.createDiagram = this.createDiagram.bind(this);
	}

	componentDidMount() {
		this.createDiagram();
    console.log(this.props.dataTemplate.shareHolders);

    let json = this.props.dataTemplate;
    const obj = JSON.parse(JSON.stringify(json));
    for(var i=0; i<5; i++)
    {obj.shareHolders[i].children = i;
    console.log(obj);}


    /*let json = this.props.dataTemplate;
    const obj = JSON.parse(JSON.stringify(json));
    obj.shareHolders[1].progr = 1;
    console.log(obj);*/
	}

	componentDidUpdate(prevProps, prevState) {
		this.createDiagram();
	}

	render() {
		return (
			<div>
				<div ref={(node) => (this.node = node)} />
			</div>
		);
	}

	createDiagram() {
		const node = this.node;
    if(!this.props.data){
      return;
    }
    if (!this.chart) {
      this.chart =  new OrgChart();
    }
     this.chart.container(node) 
        .data(this.props.data)
        .svgWidth(500)
        .initialZoom(1)
		.nodeHeight(d=> 180)
		.childrenMargin((d) => 40)
        .onNodeClick(d => console.log(d + " node clicked"))
		.nodeContent(function (d, i, arr, state) {
            return `
            <div style="padding-top:30px;background-color:none;margin-left:1px;height:${
              d.height
            }px;border-radius:2px;overflow:visible">
              <div style="height:${
                d.height - 32
              }px;padding-top:0px;background-color:white;border:2px solid lightblue;border-radius:3%">

                <img src="${user}" style="margin-top:-30px;margin-left:${d.width / 2 - 30}px;border-radius:100px;width:60px;height:60px;" />

               <div style="margin-top:-30px;background-color:none;height:10px;width:${
                 d.width - 2
               }px;border-radius:1px"></div>

               <div style="padding:20px; padding-top:35px;text-align:center">
                   <div style="color:#111672;font-size:25px;font-weight:bold"> ${obj.shareHolders} </div>
                   <div style="color:#404040;font-size:19px;margin-top:4px"> Ruolo </div>
               </div> 
               <div style="display:flex;justify-content:center;padding-left:15px;padding-right:15px;">
			   <div > Quota:  %</div>    
               </div>
              </div>     
      </div>
  `;
          })
        .render();
	}
}
