import React, {useLayuotEffect, useRef} from "react";
import { OrgChart } from "d3-org-chart";

export function Chart(props, ref){
    const d3Container = useRef(null);
    let chart = null;

    useLayuotEffect(() => {
        if(props.data && d3Container.current){
            if(!chart){
                chart = new OrgChart();
            }
            chart
                .container(d3Container.current)
                .data(props.data)
                .nodeWidth(d=>200)
                .nodeHeight(d=>200)
                .render();
        }
    }, [props.data, d3Container.current]);

    return(
        <div>
            <div ref={d3Container} />
        </div>
    )

};
