import React, { Component } from "react";
import OrgChartComponent from "./grafico/components/OrgChart";
import * as d3 from 'd3';
import { connect } from "react-redux";
import {chartData} from "./shareholders"

export default class App extends Component {
  constructor() {
    super();
    this.state = {
      data: null,
      dataTemplate: chartData
    };
  }

  /*componentDidMount(){
    console.log(this.state.data)
  }*/

  componentDidMount() {
    d3.json(
      "https://gist.githubusercontent.com/bumbeishvili/dc0d47bc95ef359fdc75b63cd65edaf2/raw/c33a3a1ef4ba927e3e92b81600c8c6ada345c64b/orgChart.json"
    ).then(data => {
        this.setState({ dataTemplate: data });
    });
  }

  render() { 
    return (
    <div className="App">
      <OrgChartComponent data={this.state.data} dataTemplate={this.state.dataTemplate}/>
    </div>
  );}
}
