import React, { Component } from "react";
import { OrgChart } from "d3-org-chart";
import user from "./user-black-shape.png";
import company from "./home-page.png";


export default class OrgChartComponent extends Component {
	constructor(props) {
		super(props);
		this.createDiagram = this.createDiagram.bind(this);
	}

	componentDidMount() {
		this.createDiagram();
	}

	componentDidUpdate(prevProps, prevState) {
		this.createDiagram();
	}

	render() {

		return (
			<div>
				<div ref={(node) => (this.node = node)} />
			</div>
		);
	}

	createDiagram() {
    let json = this.props.dataTemplate;
    let obj = JSON.parse(JSON.stringify(json));
    for(var i=1; i<6; i++){
      obj[i].nodeId = i ;
      obj[i].nodeId === 1 ? obj[i].parentNodeId = null : obj[i].parentNodeId = prevParent;
      var prevParent = obj[i].nodeId;
    }
    console.log(obj);

    var arrData = Object.values(obj);
    console.log(arrData);

    var newData = arrData.map((d)=>{
      //console.log(d.parentNodeId, d.nodeId); 
      const width =  350 //Math.round(Math.random()*50+300);
      const height = 160 // Math.round(Math.random()*20+130);
      const cornerShape = 'ORIGINAL'; //['ORIGINAL','ROUNDED','CIRCLE']
      const nodeImageWidth = 100;
      const nodeImageHeight = 100;
      const centerTopDistance = 0;
      const centerLeftDistance = 0;
      const expanded = false; //d.id=="O-6"
      const  titleMarginLeft = nodeImageWidth/2+20+centerLeftDistance
      const  contentMarginLeft = width/2+25
      return {
        //inserire qui i campi che mi servono dal JSON originale
        nodeId:d.nodeId,
        parentNodeId:d.parentNodeId,
        name:d.name,
        address:d.address.simpleValue,
        percent:d.percentSharesHeld,
        type:d.shareholderType,
        width:width,
        height:height,
        borderWidth:0.6,
        borderRadius:0,
        borderColor:{
            red:15,
            green:140,
            blue:121,
            alpha:0.7,
        },
        backgroundColor:{
          red:247,
          green:247,
          blue:249,
          alpha:0.4,
        },
        template:`<div style="color:#2A2A2A;height:160px;margin-top:-30px;background-image: linear-gradient(to right , #FFFFFF, #ECEDF0);">
                      <div style="margin-left:${titleMarginLeft+77}px;
                                  margin-top:30px;
                                  padding-top:20px;
                                  font-size:20px;
                                  font-weight:bold;
                            ">${d.name} </div>
                    <div style="margin-left:${titleMarginLeft+77}px;
                                  margin-top:3px;
                                  font-size:16px;
                            ">placeHolder </div>

                    <div style="margin-left:${titleMarginLeft+77}px;
                                  margin-top:3px;
                                  font-size:14px;
                            ">placeHolder</div>

                    <div style="margin-left:${contentMarginLeft+70}px;
                                margin-top:15px;
                                font-size:13px;
                                position:absolute;
                                bottom:5px;
                                ">
                          <div>placeHolder</div>
                          <div style="margin-top:5px">placeHolder</div>
                    </div>
                  </div>`,
        connectorLineColor:{
          red:59,
          green:60,
          blue:63,
          alpha:1
        },
        connectorLineWidth:2,
        dashArray:'',
        expanded:expanded}
        });
		const node = this.node;
    if(!this.props.data){
      return;
    }
    if (!this.chart) {
      this.chart =  new OrgChart();
    }
     this.chart.container(node) 
        .data(newData)
        .svgWidth(500)
        .initialZoom(1)
		    .nodeHeight(d=> 180)
		    .childrenMargin((d) => 40)
        .onNodeClick(d => console.log(d + " node clicked"))
        .nodeContent(function (d, i, arr, state) {
          //non entra in questo ciclo se uso i miei dati e ho più di un nodo
          console.log(d)
          return `
                <div style="padding-top:30px;background-color:none;margin-left:1px;height:${
                  d.height
                }px;border-radius:2px;overflow:visible">
                  <div style="height:${
                    d.height - 32
                  }px;padding-top:0px;background-color:white;border:3px solid lightblue;border-radius:3%">

                    <img src="${company}" style="margin-top:-30px;margin-left:${d.width / 2 - 30}px;border-radius:0px;width:60px;height:60px;" />

                  <div style="margin-top:-30px;background-color:none;height:10px;width:${
                    d.width - 2
                  }px;border-radius:1px"></div>

                  <div style="padding:20px; padding-top:50px;text-align:center">
                      <div style="color:#111672;font-size:25px;font-weight:bold"> ${d.data.name} </div>
                  </div> 
                  </div>     
          </div>
          `})
            .render();
    }
}
